mapper.select:
SELECT COUNT(1) count, T.EVENT_ID eventId, T.`STATUS` eventStatus
from V_ALARM T
WHERE T.EVENT_TYPE = #{eventType}

	AND ENTERPRISE_ID = #{enterpriseId}


	AND AREA_ID = #{areaId}

GROUP BY T.EVENT_ID, T.`STATUS`

select tt.date,SUM(tt.wdCount) wdCount,SUM(tt.sdCount) sdCount from ( SELECT DATE_FORMAT(t.ALARM_TIME,'%Y-%m-%d') date,sum(t.wd) wdCount,sum(t.sd) sdCount FROM ( select *,IF(EVENT_ID='10001' or EVENT_ID='10002',1,0) wd ,IF(EVENT_ID='10003' or EVENT_ID='10004',1,0) sd from V_ALARM WHERE EVENT_TYPE = '2' AND ALARM_TIME >= DATE_SUB(CURDATE(),INTERVAL 7 day)
AND AREA_ID = #{areaId}
AND ENTERPRISE_ID = #{enterpriseId}
)t GROUP BY DATE_FORMAT(t.ALARM_TIME,'%Y-%m-%d') union select DATE_SUB(CURDATE(),INTERVAL 6 day) date, 0 wdCount, 0 sdCount union all select DATE_SUB(CURDATE(),INTERVAL 5 day) date, 0 wdCount, 0 sdCount union all select DATE_SUB(CURDATE(),INTERVAL 4 day) date, 0 wdCount, 0 sdCount union all select DATE_SUB(CURDATE(),INTERVAL 3 day) date, 0 wdCount, 0 sdCount union all select DATE_SUB(CURDATE(),INTERVAL 2 day) date, 0 wdCount, 0 sdCount union all select DATE_SUB(CURDATE(),INTERVAL 1 day) date, 0 wdCount, 0 sdCount union all select CURDATE() date, 0 wdCount, 0 sdCount ) tt GROUP BY tt.date

select tt.date,SUM(tt.aqmCount) aqmCount,SUM(tt.kzCount) kzCount, SUM(tt.gzfCount) gzfCount ,SUM(tt.cyCount) cyCount from ( SELECT DATE_FORMAT(t.ALARM_TIME,'%Y-%m-%d') date,sum(t.aqm) aqmCount,sum(t.kz) kzCount,sum(t.gzf) gzfCount,sum(t.cy) cyCount FROM ( select *,IF(EVENT_ID='1',1,0) aqm ,IF(EVENT_ID='2',1,0) kz,IF(EVENT_ID='3',1,0) gzf,IF(EVENT_ID='4',1,0) cy from V_ALARM WHERE EVENT_TYPE = '1' AND ALARM_TIME >= DATE_SUB(CURDATE(),INTERVAL 7 day)
AND AREA_ID = #{areaId}
AND ENTERPRISE_ID = #{enterpriseId}
)t GROUP BY DATE_FORMAT(t.ALARM_TIME,'%Y-%m-%d') union select date_add(CURDATE(),interval @i:=@i-1 day) as date ,0 aqmCount,0 kzCount,0 gzfCount, 0 cyCount from ( select 1 union all select 1 union all select 1 union all select 1 union all select 1 union all select 1 union all select 1) as tmp, (select @i:= 1) t2 ) tt GROUP BY tt.date
