mapper.select:
select t1.* from T_ENTERPRISE_BASE t1 left join T_ENTERPRISE_SUPERVISION t2
on t1.ENTERPRISE_ID=t2.ENTERPRISE_ID where t1.ENTERPRISE_STATUS=3 and
t2.GRID_ID=#{gridId}

sql.where:
            EB.ENTERPRISE_STATUS=3

				  AND RG.AREA_ID = #{areaId}


				  AND RG.GRID_ID = #{gridId}


				  AND EB.ENTERPRISE_ID = #{enterpriseId}


       			 AND EC.CERTIFICATE_NO like '%${certificateNoLike}%'


                and EB.SUBJECT_CLASSIFICATION = #{subjectClassification}


                and EB.SUPERVISE_CLASSIFICATION = #{superviseClassification}

mapper.sql:
select t1.* from T_ENTERPRISE_BASE t1 left join T_ENTERPRISE_SUPERVISION t2
on t1.ENTERPRISE_ID=t2.ENTERPRISE_ID where t1.ENTERPRISE_STATUS=3 and
t2.GRID_ID=#{gridId}

mapper.select:
SELECT count( * ) total, t1.SUBJECT_CLASSIFICATION subjectClassification FROM T_ENTERPRISE_BASE t1 WHERE t1.ENTERPRISE_STATUS = 3
AND t1.AREA_ID = #{areaId}
GROUP BY t1.SUBJECT_CLASSIFICATION

SELECT count( * ) total, t1.SUPERVISE_CLASSIFICATION regulatoryLevel FROM T_ENTERPRISE_BASE t1 WHERE t1.ENTERPRISE_STATUS = 3
AND t1.AREA_ID = #{areaId}
GROUP BY t1.SUPERVISE_CLASSIFICATION
