mapper.select:
SELECT COUNT(0) FROM ( SELECT t1.*, t2.VERIFY_STATUS AS verifyStatus FROM T_ENTERPRISE_CERTIFICATE t1 INNER JOIN T_ENTERPRISE_BASE eb ON t1.ENTERPRISE_ID = eb.ENTERPRISE_ID LEFT JOIN ( SELECT * FROM T_ENTERPRISE_CERTIFICATE_CHANGE WHERE CHANGE_ID IN ( SELECT max( CHANGE_ID ) FROM T_ENTERPRISE_CERTIFICATE_CHANGE GROUP BY CERTIFICATE_ID ) ORDER BY CHANGE_ID ) t2 ON t1.CERTIFICATE_ID = t2.CERTIFICATE_ID LEFT JOIN T_ENTERPRISE_BASE t3 on t1.ENTERPRISE_ID=t3.ENTERPRISE_ID left join T_S_AREA a on t3.AREA_ID=a.AREA_ID left join T_ENTERPRISE_SUPERVISION es on t1.ENTERPRISE_ID=es.ENTERPRISE_ID left join T_RESPONSIBILITY_GRID rg on es.GRID_ID=rg.GRID_ID left join T_ENTERPRISE_VERIFY t4 on t4.CHANGE_ID=t2.CHANGE_ID
AND t1.ENTERPRISE_ID = #{enterpriseId}
AND t1.CERTIFICATE_TYPE = #{certificateType}
AND t1.CERTIFICATE_STATUS = #{certificateStatus}
and t3.AREA_ID = #{areaId}
and es.GRID_ID = #{gridId}
and t1.VALID_DATE > DATE_SUB(CURDATE(),INTERVAL -1 month)
and t1.VALID_DATE BETWEEN CURDATE() and DATE_SUB(CURDATE(),INTERVAL -1 month)
and t1.VALID_DATE < CURDATE()
and t1.VALID_DATE <= DATE_SUB(CURDATE(),INTERVAL -1 month)
AND t1.ENTERPRISE_ID in ( SELECT t1.ENTERPRISE_ID FROM T_ENTERPRISE_BASE t1 LEFT JOIN T_ENTERPRISE_SUPERVISION t2 on t1.ENTERPRISE_ID=t2.ENTERPRISE_ID LEFT JOIN T_RESPONSIBILITY_GRID t3 ON t3.GRID_ID=t2.GRID_ID where t1.ENTERPRISE_STATUS=3 and t3.MONITORS in ( SELECT PERSONNEL_ID FROM T_PERSONNEL_THREE WHERE USER_ID=#{userId} and PERSONNEL_STATUS=1) )
) t

SELECT t1.*, t2.VERIFY_STATUS AS changeStatus,t3.ENTERPRISE_NAME enterpriseName,t3.REGISTER_ADDRESS registerAddress,t4.VERIFY_CONCLUSION verifyConclusion,t2.CHANGE_ID changeId FROM T_ENTERPRISE_CERTIFICATE t1 INNER JOIN T_ENTERPRISE_BASE eb ON t1.ENTERPRISE_ID = eb.ENTERPRISE_ID LEFT JOIN ( SELECT * FROM T_ENTERPRISE_CERTIFICATE_CHANGE WHERE CHANGE_ID IN ( SELECT max( CHANGE_ID ) FROM T_ENTERPRISE_CERTIFICATE_CHANGE GROUP BY CERTIFICATE_ID ) ORDER BY CHANGE_ID ) t2 ON t1.CERTIFICATE_ID = t2.CERTIFICATE_ID LEFT JOIN T_ENTERPRISE_BASE t3 on t1.ENTERPRISE_ID=t3.ENTERPRISE_ID left join T_S_AREA a on t3.AREA_ID=a.AREA_ID left join T_ENTERPRISE_SUPERVISION es on t1.ENTERPRISE_ID=es.ENTERPRISE_ID left join T_RESPONSIBILITY_GRID rg on es.GRID_ID=rg.GRID_ID left join T_ENTERPRISE_VERIFY t4 on t2.CHANGE_ID=t4.CHANGE_ID
AND t1.ENTERPRISE_ID = #{enterpriseId}
AND t1.CERTIFICATE_TYPE = #{certificateType}
AND t1.CERTIFICATE_STATUS = #{certificateStatus}
and t3.AREA_ID = #{areaId}
and es.GRID_ID = #{gridId}
and t1.VALID_DATE > DATE_SUB(CURDATE(),INTERVAL -1 month)
and t1.VALID_DATE BETWEEN CURDATE() and DATE_SUB(CURDATE(),INTERVAL -1 month)
and t1.VALID_DATE < CURDATE()
and t1.VALID_DATE <= DATE_SUB(CURDATE(),INTERVAL -1 month)
AND t1.ENTERPRISE_ID in ( SELECT t1.ENTERPRISE_ID FROM T_ENTERPRISE_BASE t1 LEFT JOIN T_ENTERPRISE_SUPERVISION t2 on t1.ENTERPRISE_ID=t2.ENTERPRISE_ID LEFT JOIN T_RESPONSIBILITY_GRID t3 ON t3.GRID_ID=t2.GRID_ID where t1.ENTERPRISE_STATUS=3 and t3.MONITORS in ( SELECT PERSONNEL_ID FROM T_PERSONNEL_THREE WHERE USER_ID=#{userId} and PERSONNEL_STATUS=1) )
